import './Peminjaman.css';
import axios from 'axios';
import React from 'react';
import { useParams } from 'react-router-dom';

function PeminjamanDelete() {
    const { id } = useParams();

    // Define state
    const [formValue, setFormValue] = React.useState({
        id_peminjam: '',
        nama_peminjaman: '',
        judul_buku: '',
        durasi_peminjaman: ''
    });

    // UseEffect hook
    React.useEffect(() => {
        // Call method "fetchData"
        fetchData();
    }, []);

    // Function "fetchData"
    const fetchData = async () => {
        try {
            // Fetching data
            const response = await axios.get(`https://localhost:7245/GetPeminjamanByNama?Id=${id}`);
            // Get response data
            const data = response.data.data;
            // Assign response data to state "formValue"
            setFormValue(data);
            console.log(data);
        } catch (error) {
            console.error('Error fetching data:', error);
            alert('Data tidak ditemukan atau sudah dihapus!');
        }
    }

    const handleChange = (event) => {
        setFormValue({
            ...formValue,
            [event.target.name]: event.target.value
        });
    }

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            // Make axios DELETE request
            const response = await axios.delete(`https://localhost:7245/DeletePeminjaman/${id}`, {
                headers: { "Content-Type": "application/json" }
            });
            console.log(response);
            alert('Data berhasil dihapus');
        } catch (error) {
            console.error('Error deleting data:', error);
            alert('Gagal menghapus data: ' + error.message);
        }
    }

    return (
        <div className="card">
            <div className="container">
                <div className="Titel">
                    Hapus Data Peminjaman {}
                </div>
                <div className="conten">
                    <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        name="id_peminjam"
                        placeholder="enter ID"
                        value={formValue.id_peminjam}
                        onChange={handleChange}
                    /><br/><br/>
                    <input
                        type="text"
                        name="nama_peminjaman"
                        placeholder="enter a Nama"
                        value={formValue.nama_peminjaman}
                        onChange={handleChange}
                    /><br/><br/>
                    <button type="submit" className='btn btn-danger'>Hapus</button>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default PeminjamanDelete;
