        },
        {
            name: 'Judul Buku',
            selector: row => row.judul_buku,
            sortable:true
        },
        {
            name: 'Durasi Peminjaman',
            selector: row => row.durasi_peminjaman,
            sortable:true
        },
        {
            name: 'Denda',
            selector: row => row.denda,
            sortable:true
        },
        {
            name: 'Ubah',
            selector: row => <Link to={"/databuku_edit/"+row.id_peminjam} className='btn btn-primary'>Edit</Link>,
            sortable:true
        },
        {
            name: 'Hapus',
            selector: row => <Link to={"/databuku_delete/"+row.id_peminjam} className='btn btn-danger'>Delete</Link>,
            sortable:true
        },
    ];
    
    return (
        <div className='card'>
            <div className='container'>
                <div className='Titel'>
                    Buku
                </div>
                <div className='content'>
                    Selamat Datang Di Hanabi Library
                </div>
            </div>
        </div>
    );
}

export default Buku;
