import './Peminjaman.css';
import axios from 'axios';
import React from 'react';

function PeminjamAdd() {

    const [formValue, setformValue] = React.useState({
        id: '',
        nama_peminjam: '',
        judul_buku_pinjaman: '',
        waktu_peminjaman: '',
        durasi_peminjaman: ''
        });

    const handleChange = (event) => {
        setformValue({
            ...formValue,
            [event.target.name]: event.target.value
        });
    }

    const handleSubmit = async() => {
        // store the states in the form data
        const FormDataInput = new FormData();
        FormDataInput.append("id", formValue.id)
        FormDataInput.append("nama_peminjam", formValue.nama_peminjam)
        FormDataInput.append("judul_buku", formValue.judul_buku_pinjaman)
        FormDataInput.append("waktu_peminjaman", formValue.waktu_peminjaman)
        FormDataInput.append("durasi_peminjaman", formValue.durasi_peminjaman)
        alert('Data berhasil disimpan')
        try {
            // make axios post request
            const response = await axios({
            method: "post",
            url: 'https://localhost:7245/CreatePeminjaman',
            data: FormDataInput,
            headers: { "Content-Type": "application/json" },
        });
            console.log(response)
        } catch(error) {
        console.log(error)
        alert(error)
        }
    }

    return (
        <div className="card">
            <div className="container">
                <div className="Titel">
                    Tambah Data Peminjam
                </div>
                <div className="conten">
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        name="id"
                        placeholder="enter ID"
                        value={formValue.id}
                        onChange={handleChange}
                    /><br/><br/>
                    <input
                        type="text"
                        name="nama_peminjam"
                        placeholder="enter a Nama"
                        value={formValue.nama_peminjaman}
                        onChange={handleChange}
                    /><br/><br/>
                    <input
                        type="text"
                        name="judul_buku"
                        placeholder="enter a Judul"
                        value={formValue.judul_buku}
                        onChange={handleChange}
                    /><br/><br/>
                    <input
                        type="text"
                        name="durasi_peminjaman"
                        placeholder="enter a Durasi"
                        value={formValue.durasi_peminjaman}
                        onChange={handleChange}
                    /><br/><br/>
                    <button type="submit" className='btn btn-primary'> Simpan </button>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default PeminjamAdd;
